import * as i0 from "@angular/core";
/*
* Serviço responsável por realizar a chamada a API
* @author Vinicius Braga Cunha <cunha.vinicius@outlook.com.br>
*/
export class CalculadoraService {
    constructor() { }
    async calcular(sinal, num1, num2, token) {
        try {
            token = localStorage.getItem('auth_token');
            const response = await fetch('/api/operacao', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ num1, num2, sinal }),
            });
            if (!response.ok) {
                throw new Error('Erro na chamada da API');
            }
            const data = await response.json();
            console.log(data);
            return data['data'];
        }
        catch (error) {
            console.log('Erro:', error);
            return 0;
        }
    }
}
