import { __decorate } from "tslib";
import { Injectable } from '@angular/core';
/*
* Serviço responsável por realizar a chamada a API
* @author Vinicius Braga Cunha <cunha.vinicius@outlook.com.br>
*/
let CalculadoraService = class CalculadoraService {
    constructor() { }
    async calcular(sinal, num1, num2, token) {
        try {
            const response = await fetch('https://172.28.32.1:44665/operacao', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ num1, num2, sinal }),
            });
            if (!response.ok) {
                throw new Error('Erro na chamada da API');
            }
            const data = await response.json();
            return data.resultado;
        }
        catch (error) {
            console.error('Erro:', error);
            return 0;
        }
    }
};
CalculadoraService.SOMA = '+';
CalculadoraService.SUBTRACAO = '-';
CalculadoraService.DIVISAO = '/';
CalculadoraService.MULTIPLICACAO = '*';
CalculadoraService.ctorParameters = () => [];
CalculadoraService = __decorate([
    Injectable({
        providedIn: 'root'
    })
], CalculadoraService);
export { CalculadoraService };
