import * as i0 from "@angular/core";
/*
* Serviço responsável por realizar a chamada a API
* @author Vinicius Braga Cunha <cunha.vinicius@outlook.com.br>
*/
export class CalculadoraService {
    constructor() { }
    async calcular(sinal, num1, num2) {
        try {
            const response = await fetch('https://172.28.32.1:44665/operacao', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ num1, num2, sinal }),
            });
            if (!response.ok) {
                throw new Error('Erro na chamada da API');
            }
            const data = await response.json();
            return data.resultado; // Supondo que o resultado esteja nesta chave
        }
        catch (error) {
            console.error('Erro:', error);
            return 0; // Valor padrão em caso de erro
        }
    }
}
